# Méthode pour le calcul total
def total(handle)
  puts "Il y a #{handle.count} journalistes véreux dans ce fichier."
  perform
end

# Méthode pour le tri dans l'odre alphabétique
def tri_a_z(handle)
  handle_a_z = handle.sort_by {|element| element.size}
end

# Méthode pour le tri croissant par nombre de caractères et afficher le 1er
def min_handle(handle_1_2)
  puts "Le journaliste au nom le plus court est #{handle_1_2.first} (il a surement la plus courte également...)"
  perform
end

# Méthode pour afficher les handles de 5 caractères
def tri_5_caracter(handle)
  puts "Il y a #{handle.count {|element| element.size==6}} noms avec 5 caractères. Why not?"
  perform
end

# Méthode pour afficher le nombre de handles commencant par une maj
def tri_majuscule(handle)
  handle_withoutA = handle.each {|element| element.slice!(0)}
  puts "Il y a #{handle_withoutA.count{|element| element[0]=~ /[A-Z]/}} noms qui commencent par une majuscule"
  perform
end

# Méthode pour trier par ordre alphabétique
def handle_a_z (handle)
  puts "Voici l'ordre alphabétique des journalistes:"
  puts handle.sort_by(&:downcase)
  perform
end

# Méthode pour le tri croissant par nombre de caractères
def handle_by_size(handle_1_2)
  puts "Voici les noms par ordre de taille:"
  puts handle_1_2
  perform
end 

# Méthode pour afficher l'index d'un handle
def where_is_he (handle)
  puts "@epenser est à la position #{handle.index("@epenser")+1} du notre super tableau"
  perform
end

# Méthode pour répartir les handles par nombre de caractères
def repartition_size(handle)
  puts "Les handles de quel nombre de caractère souhaites-tu afficher?"
  print "> "
  number=gets.chomp.to_i
  puts "Voici la liste de handles à #{number} caractères"
  puts total= handle.select {|element| element.size==number+1 }
  puts "Il y en a au total #{total.count}"
  puts ""
  puts "Comme je suis de bonne humeur je te donne tous les totaux :"
  numb=3
  until numb == 33
    puts "Longueur #{numb} caractères ==> #{handle.select {|element| element.size==numb+1 }.count}"
    numb+=1
  end
  perform
end

# Méthode menu + lancer vers les méthodes
def perform
  handle = journalist
  handle_1_2 =tri_a_z(handle)
  puts "___________________________________________________________________"
  puts "                 Bonjour, que voulez vous savoir ?                 "
  puts ""
  puts "|> Le nombre de handle dans la liste :               \033[1;32m> Tapes 1 <"+"\033[0m"
  puts "|> Le handle le plus court :                         \033[1;32m> Tapes 2 <"+"\033[0m"
  puts "|> Combien il y a de handle contenant 5 caractères : \033[1;32m> Tapes 3 <"+"\033[0m"
  puts "|> Combien de handle qui commencent par une maj :    \033[1;32m> Tapes 4 <"+"\033[0m"
  puts "|> La liste des handles dans l'ordre alphabétique :  \033[1;32m> Tapes 5 <"+"\033[0m"
  puts "|> Trier les handles par taille :                    \033[1;32m> Tapes 6 <"+"\033[0m"
  puts "|> La position de @epenser dans la liste :           \033[1;32m> Tapes 7 <"+"\033[0m"
  puts "|> Une répartition des handles par tailles :         \033[1;32m> Tapes 8 <"+"\033[0m"
  puts "|> Pour quitter                                      \033[1;41m> Tapes 0 <"+"\033[0m"
  puts "___________________________________________________________________"
  print ">>>> "
  user_choose=gets.chomp.to_i
  if user_choose==1
    total(handle)
    elsif user_choose==2
      min_handle(handle_1_2)
    elsif user_choose==3
      tri_5_caracter(handle)
    elsif user_choose==4
      tri_majuscule(handle)
    elsif user_choose==5
      handle_a_z(handle)
    elsif user_choose==6
      handle_by_size(handle_1_2)
    elsif user_choose==7
      where_is_he(handle)
    elsif user_choose==8
      repartition_size(handle)  
  end
end

perform